mapper.select:
select ti.TRANS_DETAIL_ID, ti.REQUEST_NO, ti.REQUEST_TIME, ti.TRANS_FLOW_NO, ti.ORDER_NO, ti.RELATE_ORDER_NO, ti.ORDER_PACKAGE_NO, ti.ORDER_DATE, ti.ORDER_AMOUNT, ti.ORDER_COUNT, ti.ORDER_TYPE, ti.TRANS_TYPE, ti.FUNC_CODE, ti.DEAL_PRODUCT_CODE, ti.USER_ID, ti.INTER_MERCHANT_CODE, ti.MERCHANT_CODE, ti.INTO_INST_CODE, ti.AMOUNT, ti.FEE_AMOUNT, ti.FEE_AMOUNT1, ti.FEE_AMOUNT2, ti.FEE_AMOUNT3, ti.FEE_AMOUNT4, ti.PROFIT, ti.FC_AMOUNT, ti.BUSINESS_TYPE, ti.PAY_CHANNEL_ID, ti.BANK_CODE, ti.USER_IP_ADDRESS, ti.ERROR_CODE, ti.ERROR_MSG, ti.PRODUCT_ID, ti.PRODUCT_W_ID, ti.CANCEL_IND, ti.DATA_FROM, ti.DELIVER_STATUS_ID, ti.INVOICE_NO, ti.OBLIGATE1, ti.OBLIGATE2, ti.OBLIGATE3, ti.STATUS_ID, ti.READ_STATUS_ID, ti.DFLAG, ti.REMARK, ti.ACCOUNT_DATE, ti.REQUEST_ID, ti.ROLE_CODE, ti.INTER_ROLE_CODE, ti.REFER_USER_ID, ti.REFER_ROLE_CODE, ti.REFER_PRODUCT_ID, ti.OTHER_AMOUNT, ti.USER_FEE_WAY, ti.INTEREST, ti.PAY_WAY, ti.PROCESS_STATUS, ti.ORIGINAL_ORDER_PACKAGE_NO, ti.ORIGINAL_ORDER_ID, ti.CURRENCY, ti.REVERSE_FLAG, ti.CANCEL_TAG, ti.ACCEPT_INST_CODE, ti.ACCEPT_INST_ID, ti.TARGET_MERCHANT_CODE, ti.TARGET_TERMINAL_CODE, ti.CHANNEL_RETURN_CODE, ti.RETURN_MEG, ti.RECEIPT_SERIAL_NO, ti.MCC, ti.AUTH_CODE, ti.CHANNEL_INFO, ti.TRADE_ESB_NO, ti.RESERVE_1, ti.RESERVE_2, ti.RESERVE_3, ti.READ_CREATED_TIME from SETTLE_POS_DETAIL ti where 1=1 and ti.ORDER_TYPE = #{orderType} and ti.FUNC_CODE in (${funcCode}) and ti.status_id in (11,22,24)
and ti.MERCHANT_CODE in (${merchantCode})
and ti.BUSINESS_TYPE = #{businessType}
and ti.READ_STATUS_ID = #{readStatusId}
and ti.READ_STATUS_ID != #{obligate2,jdbcType=TINYINT}
and ( ti.PAY_CHANNEL_ID = #{payChannelId}
or ti.PAY_CHANNEL_ID is NULL
) order by ti.REQUEST_TIME
